-- classDiagram
--     T_1 <|-- T_2
--     T_2 <|-- T_3
--     T_2 <|-- T_4
CREATE TABLE T_1 (
    PK_11 INT64 NOT NULL,
) PRIMARY KEY (PK_11);

CREATE TABLE T_2 (
    PK_11 INT64 NOT NULL,
    PK_21 INT64 NOT NULL,
) PRIMARY KEY (PK_11, PK_21),
    INTERLEAVE IN PARENT T_1 ON DELETE CASCADE;

CREATE TABLE T_3 (
    PK_11 INT64 NOT NULL,
    PK_21 INT64 NOT NULL,
    PK_31 INT64 NOT NULL,
) PRIMARY KEY (PK_11, PK_21, PK_31),
    INTERLEAVE IN PARENT T_2 ON DELETE CASCADE;

CREATE TABLE T_4 (
    PK_11 INT64 NOT NULL,
    PK_21 INT64 NOT NULL,
    PK_41 INT64 NOT NULL,
) PRIMARY KEY (PK_11, PK_21, PK_41),
    INTERLEAVE IN PARENT T_2 ON DELETE CASCADE;
