-- classDiagram
--     T_1 <|-- T_2
--     T_2 <|-- T_3
--     T_3 <|-- T_1
CREATE TABLE T_1 (
    PK_11 INT64 NOT NULL,
    PK_12 INT64 NOT NULL,
) PRIMARY KEY (PK_11, PK_12);

CREATE TABLE T_2 (
    PK_21 INT64 NOT NULL,
    PK_22 INT64 NOT NULL,
    CONSTRAINT FK_2_1 FOREIGN KEY (PK_21, PK_22) REFERENCES T_1 (PK_11, PK_12),
) PRIMARY KEY (PK_21, PK_22);

CREATE TABLE T_3 (
    PK_31 INT64 NOT NULL,
    PK_32 INT64 NOT NULL,
    CONSTRAINT FK_3_2 FOREIGN KEY (PK_31, PK_32) REFERENCES T_2 (PK_21, PK_22),
) PRIMARY KEY (PK_31, PK_32);

ALTER TABLE T_1 ADD CONSTRAINT FK_1_3 FOREIGN KEY (PK_11, PK_12) REFERENCES T_3 (PK_31, PK_32);
